mapper.select:

	SELECT *
	    FROM (
	        SELECT A.*
	              ,ROWNUM AS RNUM
	              ,COUNT(*) OVER() AS TOTCNT
	            FROM (
	                SELECT *
	                    FROM BOOK
	                    WHERE UPPER(TITLE) LIKE '%' || UPPER(#{searchBook}) || '%'
	                ) A
	    )
	    WHERE RNUM > #{pageStart}
	      AND RNUM <= #{pageEnd}

mapper.insert:

	INSERT INTO CATEGORY
	(
		CATEGORY_ID, CATEGORY_NAME
	)
	VALUES
	(
		CATEGORY_SEQ.NEXTVAL, #{category_name}
	)

INSERT INTO MEMBER
(
	USER_ID, PASSWORD, NAME, ADDRESS, POST_CODE, PHONE
)
VALUES
(
	#{user_id}, #{password}, #{name}, #{address}, #{post_code}, #{phone}
)
